/**
 * Renders input to a string.
 *
 * Works like outerHTML by rendering a VTree and returning the source. This
 * will accept any input that `outerHTML` normally accepts. This is a true
 * render, but omits the DOM patching task.
 *
 * @param {ValidInput} input
 * @param {TransactionConfig} config
 * @return {string}
 */
export default function toString(input: ValidInput, config?: TransactionConfig): string;
export type ValidInput = import('./util/types').ValidInput;
export type TransactionConfig = import('./util/types').TransactionConfig;
export type VTree = import('./util/types').VTree;
export type VTreeAttributes = import('./util/types').VTreeAttributes;
